import React, { Component } from 'react'
import {
  BrowserRouter as Router,
  Link,
  Route
} from 'react-router-dom';


export default class Product extends Component {
  constructor(props) {
    super(props);
    this.state = {
      category: '',
      proizvodi: []
    }

  }

  componentDidMount = () => {



    let url = '';
    if(this.state.category !== {}){
      fetch('/product/${this.state.category}')
      .then(res => res.json())
      .then(proizvodi => this.setState({proizvodi: proizvodi}));
    }else{
      fetch('/product')
      .then(res => res.json())
      .then(proizvodi => this.setState({proizvodi: proizvodi}));
    }
  }
  componentWillUpdate = (prevProps, prevState) => {
      let url = '';
    if (prevState.category !== this.state.category){
      url = '/product/' + this.state.category;

    }else{
      url = '/product/' + prevState.category;

    }
  }



  render() {
    console.log(this.state.category);
    return (
      <Router>
        <div>

          <h1>Proizvodi {this.state.proizvodi.name}</h1>

        </div>
      </Router>
    )
  }
}
