    //   .then(proizvodi => this.setState({proizvodi: proizvodi}));
    // }
  }


  render() {
    console.log(this.props.match.params);
    return (
      <Router>
        <div>

          <h1>Proizvodi {this.state.proizvodi.name}</h1>

        </div>
      </Router>
    )
  }
}
