import React, { Component } from 'react'
import {
  BrowserRouter as Router
} from 'react-router-dom';
import Slicica from './Slicica';
var url = '';
var proi = [];


export default class Product extends Component {

  constructor(props) {

    super(props);
    this.state = {
      category: '',
      proizvodi: [],
    }

  }


  componentDidMount = () => {

    if (this.props.match.params.category !== this.state.category) {
      this.setState({
        category: this.props.match.params.category
      });
    }

    url = '/product';

    fetch(url)
      .then(res => res.json())
      .then(proizvodi => {
        this.setState({
          proizvodi: proizvodi
        }
        );
      });
  }


  componentDidUpdate = () => {
    if (this.props.match.params.category !== this.state.category) {
      this.setState({
        category: this.props.match.params.category
      });
    }


  }


  render() {
    console.log(this.state.proizvodi);



    return (
      <Router>
        <div>
          <ul>
            {this.state.proizvodi.map(pro => {
              if(pro.category === 'langery'){
                return <h1>langerie</h1>
              }else if(pro.sale){
                return <h1>Sale</h1>
              }else {
                return <h1>Nesto je drugo</h1>
              }
            })}
          </ul>
        </div>
      </Router>
    )
  }
}
