import React, { Component } from 'react'
import {
  BrowserRouter as Router
} from 'react-router-dom';
import Slicica from './Slicica';
import { Container, Row, Col } from 'reactstrap';

var url = '';


export default class Product extends Component {

  constructor(props) {

    super(props);
    this.state = {
      category: '',
      proizvodi: [],
    }

  }


  componentDidMount = () => {

    if (this.props.match.params.category !== this.state.category) {
      this.setState({
        category: this.props.match.params.category
      });
    }

    url = '/product';

    fetch(url)
      .then(res => res.json())
      .then(proizvodi => {
        this.setState({
          proizvodi: proizvodi
        }
        );
      });
  }


  componentDidUpdate = () => {
    if (this.props.match.params.category !== this.state.category) {
      this.setState({
        category: this.props.match.params.category
      });
    }


  }


  render() {
    console.log(
      this.state.category
    );




    return (
      <Container >
        <Row ><Col>oooooo</Col></Row>
        <Row>
          <Col xs='2'>nest</Col>

          <Col>
            {this.state.proizvodi.map(pro => {
              if (this.state.category !== undefined) {
                if (pro.category === this.state.category) {
                return <div className='col-4' style={{float: "left", marginBottom: '20px'}}><Slicica className='col-3' imageSrc={pro.imgSrc} category={pro.category} name={pro.name} description={pro.description} /></div>

                } if (this.state.category === 'sale') {
                  if (pro.sale) {
                return <div className='col-4' style={{float: "left", marginBottom:'20px'}}><Slicica className='col-3' imageSrc={pro.imgSrc} category={pro.category} name={pro.name} description={pro.description} /></div>
                  }
                }
              }
              else {
                return <div className='col-4' style={{float: "left", marginBottom:"20"}}><Slicica className='col-3' imageSrc={pro.imgSrc} category={pro.category} name={pro.name} description={pro.description} /></div>
              }
            })}
            </Col>


        </Row>


      </Container>
    )
  }
}
