import React, { Component } from 'react'
import Slicica from './Slicica';
import { Container, Row, Col,Button , Modal, ModalHeader, ModalBody, ModalFooter, Jumbotron} from 'reactstrap';

var url = '';


export default class Product extends Component {

  constructor(props) {

    super(props);
    this.state = {
      category: '',
      proizvodi: [],
      modal: false
    }
    this.toggle = this.toggle.bind(this);

  }
  toggle() {
    this.setState({
      modal: !this.state.modal
    });
  }

  componentDidMount = () => {

    if (this.props.match.params.category !== this.state.category) {
      this.setState({
        category: this.props.match.params.category
      });
    }

    url = '/product';

    fetch(url)
      .then(res => res.json())
      .then(proizvodi => {
        this.setState({
          proizvodi: proizvodi
        }
        );
      });
  }


  componentDidUpdate = () => {
    if (this.props.match.params.category !== this.state.category) {
      this.setState({
        category: this.props.match.params.category
      });
    }


  }


  render() {
    console.log(
      this.state.category
    );




    return (
      <div>
        <Container className="mb-2 mt-5" >
          <Row style={{height: '10'}}><Jumbotron /></Row>
          <Row>
            <Col xs='2'><p>Ovde ide sort</p></Col>

            <Col>
              {this.state.proizvodi.map(pro => {
                if (this.state.category !== undefined) {
                  if (pro.category === this.state.category) {
                    return <div onClick={this.toggle} className='col-4 my-2' style={{ float: "left" }}><Slicica className='col-3' imageSrc={pro.imgSrc} category={pro.category} name={pro.name} description={pro.description} /></div>

                  } if (this.state.category === 'sale') {
                    if (pro.sale) {
                      return <div onClick={this.toggle} className='col-4 my-2' style={{ float: "left" }}><Slicica className='col-3' imageSrc={pro.imgSrc} category={pro.category} name={pro.name} description={pro.description} /></div>
                    }
                  }
                }
                else {
                  return <div onClick={this.toggle} className='col-4 my-2' style={{ float: "left" }}><Slicica className='col-3' imageSrc={pro.imgSrc} category={pro.category} name={pro.name} description={pro.description} /></div>
                }
              })}
            </Col>


          </Row>


        </Container>

        <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
          <ModalHeader toggle={this.toggle}>Modal title</ModalHeader>
          <ModalBody>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
          </ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={this.toggle}>Do Something</Button>{' '}
            <Button color="secondary" onClick={this.toggle}>Cancel</Button>
          </ModalFooter>
        </Modal>
      </div>
    )
  }
}
