import React, { Component } from 'react'
import {
  BrowserRouter as Router
} from 'react-router-dom';
import Slicica from './Slicica';
var url = '';
var proi = [];


export default class Product extends Component {

  constructor(props) {

    super(props);
    this.state = {
      category: '',
      proizvodi: [],
    }

  }


  componentDidMount = () => {

    if (this.props.match.params.category !== this.state.category) {
      this.setState({
        category: this.props.match.params.category
      });
    }

    url = '/product';

    fetch(url)
      .then(res => res.json())
      .then(proizvodi => {
        this.setState({
          proizvodi: proizvodi
        }
        );
      });
  }


  componentDidUpdate = () => {
    if (this.props.match.params.category !== this.state.category) {
      this.setState({
        category: this.props.match.params.category
      });
    }
    proi= [];
    for (let index = 0; index < this.state.proizvodi.length; index++) {
       if(this.state.proizvodi[index].category === this.state.category){
         proi.push(this.state.proizvodi[index]);
       }

    }
  }





  componentWillUnmount() {
    console.log('Unmount');

  }
  render() {
    console.log(this.state.proizvodi);

    console.log(proi);

    return (
      <Router>
        <div>
          <ul>
          </ul>
        </div>
      </Router>
    )
  }
}
